mapper.select:
SELECT COUNT(*) 
FROM voj_contest_contestants
WHERE contest_id = #{contestId}

SELECT *
FROM voj_contest_contestants
WHERE contest_id = #{contestId}
AND contestant_uid = #{contestantUid}

mapper.update:
